    public function testProgressBarOutput(): void
    {
        $progressBar = new ProgressBar(100);
        $progressBar();
        // Suppress  output to console
        $this->expectOutputString($this->output_string);
    }

    public function testProgressBarLogger(): void
    {
        $logger = new Logger('test');
        // Добавляем обработчик для вывода логов в стандартный поток вывода
        $handler = new StreamHandler("php://stdout", Level::Debug);
        $formatter = new LineFormatter("%level_name% > %message% %context% %extra%\n", '');
        $handler->setFormatter($formatter);
        // Добавляем процессор для добавления замыканий
        $processor = new ClosureContextProcessor ();
        $logger->pushProcessor($processor);
        $logger->pushHandler($handler);

        stream_filter_register("intercept", Intercept::class);
        $logger->warning('just activate');
        //start stream
        $stderr = $handler->getStream();
        if (is_resource($stderr)) {
            stream_filter_append($stderr, "intercept");
        }

        $progressBar = new ProgressBar(100);
        $logger->info('ProgressBar', [$progressBar()]);

        $this->assertSame(
            Intercept::$cache,
            'INFO > ProgressBar [{"LTT\\\\Console\\\\ProgressBar":[]}] []'.PHP_EOL,
        );

        $this->expectOutputString($this->output_string);
    }
